import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  url: 'https://tonejs.github.io/audio/drum-samples/acoustic-kit/hihat.mp3',
  mute: true,
  volume: 1,
  autostart: true,
  loop: true
  // loopStart: 0.2,
  // loopEnd: 0.6,
  // playbackRate: 0.8
}

const player = new Tone.Player(playerSettings)
const filter = new Tone.AutoFilter(1).start()
player.chain(filter, Tone.Destination)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Hihat Sound',
    type: 'Player',
    node: player,
    settings: playerSettings
  }
]

export { instrument }
