import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const synthSettings = {
  volume: 0,
  detune: 0,
  portamento: 0,
  envelope: {
    attack: 0,
    attackCurve: 'linear',
    decay: 0,
    decayCurve: 'linear',
    sustain: 0,
    release: 1,
    releaseCurve: 'sine'
  },
  oscillator: {
    type: 'smooth',
    modulationType: 'smooth',
    // partialCount: 0,
    // partials: [],
    phase: 0,
    harmonicity: 0
  }
}

const reverbSettings = {
  wet: 1,
  decay: 7.15,
  preDelay: 0.01
}

const channelSettings = {
  volume: -14,
  pan: 0,
  mute: false,
  solo: false
}

// function instrument() {
const synthNode = new Tone.Synth(synthSettings)
const reverbNode = new Tone.Reverb(reverbSettings)
// const chorusNode = new Tone.Chorus(chorusSettings).start()
// const freeverbNode = new Tone.Freeverb(freeverbSettings)
// const pingPongDelayNode = new Tone.PingPongDelay(pingPongDelaySettings)
// const tremoloNode = new Tone.Tremolo(tremoloSettings)
const channelNode = new Tone.Channel(channelSettings).toDestination()

synthNode.chain(reverbNode, channelNode)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Melody Synth',
    type: 'ToneSynth',
    node: synthNode,
    settings: synthSettings
  },
  {
    id: generateUniqId(),
    name: 'Reverb',
    type: 'ReverbEffect',
    node: reverbNode,
    settings: reverbSettings
  },
  {
    id: generateUniqId(),
    name: 'Channel',
    type: 'Channel',
    node: channelNode,
    settings: channelSettings
  }
]

const v = 1

const sequention = new Tone.Sequence(
  (time, note) => {
    synthNode.triggerAttackRelease(note, '1m', time)
  },
  ['G4', 'F4'],
  '1m'
)

export { instrument, sequention }
