import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  url: 'https://https://tonejs.github.io/audio/drum-samples/KPR77/kick.mp3',
  mute: true,
  volume: -3,
  autostart: true,
  loop: true,
  playbackRate: 0.4
}

// const playerSettings = {
//   url: 'https://tonejs.github.io/audio/loop/FWDL.mp3',
