import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  url: 'https://tonejs.github.io/audio/drum-samples/Techno/hihat.mp3',
  mute: true,
  volume: 1,
  autostart: true,
  loop: true,
  loopStart: 0.2,
  loopEnd: 0.6,
  playbackRate: 0.8
}

// const player = new Tone.Player("https://tonejs.github.io/audio/drum-samples/acoustic-kit/hihat.mp3").toDestination();
// player.loop = true;
// // sync the source so that it plays between 0 and 0.3 on the Transport's timeline

const player = new Tone.Player(playerSettings)

player.chain(Tone.Destination)

player.sync().start(0).stop(0.3)
// start the transport.
Tone.Transport.start()
// set it to loop once a second
Tone.Transport.loop = true
Tone.Transport.loopEnd = 1

const instrument = [
  {
    id: generateUniqId(),
    name: 'Hihat Sound',
    type: 'Player',
    node: player,
    settings: playerSettings
  }
]

export { instrument }
