import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const synthSettings = {
  volume: 0,
  detune: 0,
  portamento: 0,
  envelope: {
    attack: 0.14,
    attackCurve: 'step',
    decay: 0.5,
    decayCurve: 'exponential',
    sustain: 0.65,
    release: 0.5,
    releaseCurve: 'cosine'
  },
  oscillator: {
    type: 'fatsine',
    modulationType: 'fatsine',
    // partialCount: 0,
    // partials: [],
    phase: 0,
    harmonicity: 0
  }
}

const reverbSettings = {
  wet: 1,
  decay: 0,
  preDelay: 0
}

const channelSettings = {
  volume: 1,
  pan: 0,
  mute: false,
  solo: false
}

const synthNode = new Tone.Synth(synthSettings)

const reverbNode = new Tone.Reverb(reverbSettings)

const channelNode = new Tone.Channel(channelSettings).toDestination()

synthNode.chain(reverbNode, channelNode)

const instrument = [
  {
    id: generateUniqId(),
    name: 'All effect Synth',
    type: 'ToneSynth',
    node: synthNode,
    settings: synthSettings
  },
  {
    id: generateUniqId(),
    name: 'Reverb',
    type: 'ReverbEffect',
    node: reverbNode,
    settings: reverbSettings
  },
  {
    id: generateUniqId(),
    name: 'Channel',
    type: 'Channel',
    node: channelNode,
    settings: channelSettings
  }
]

// prettier-ignore nathan taylor 10.7PO sync
const v = 1

const part = new Tone.Part(
  function (time, note) {
    synthNode.triggerAttackRelease(
      note.noteName,
      note.duration,
      time,
      note.velocity
    )
  },
  [
    {
      time: '0:0:0',
      noteName: 'A4#',
      velocity: 1,
      duration: '4n'
    },
    {
      time: '0:1:0',
      noteName: 'C5',
      velocity: 1,
      duration: '2n'
    },
    {
      time: '0:3:0',
      noteName: 'A4#',
      velocity: 1,
      duration: '4n'
    },
    {
      time: '1:0:0',
      noteName: 'D5',
      velocity: 1,
      duration: '2n'
    },
    {
      time: '1:3:0',
      noteName: 'C5',
      velocity: 1,
      duration: '4n'
    },
    {
      time: '1:4:0',
      noteName: 'A4#',
      velocity: 1,
      duration: '8n'
    },
    {
      time: '2:0:0',
      noteName: 'D5',
      velocity: 1,
      duration: '2n'
    },
    {
      time: '2:1:0',
      noteName: 'C5',
      velocity: 1,
      duration: '4n'
    },
    {
      time: '2:2:0',
      noteName: 'C5',
      velocity: 1,
      duration: '4n'
    }
  ]
)

part.loopEnd = '4m'
part.loop = true

export { instrument, part }
