import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const synthSettings = {
  volume: 0.8,
  detune: 0,
  portamento: 0,
  envelope: {
    attack: 0.05,
    attackCurve: 'bounce',
    decay: 3,
    decayCurve: 'exponential',
    sustain: 0,
    release: 1,
    releaseCurve: 'linear'
  },
  oscillator: {
    type: 'amtriangle',
    modulationType: 'sine',
    // partialCount: 0,
    // partials: [],
    phase: 0,
    harmonicity: 0
  }
}

const reverbSettings = {
  wet: 0.5,
  decay: 7.15,
  preDelay: 0.01
}

const channelSettings = {
  volume: -14,
  pan: 0,
  mute: false,
  solo: false
}

const synthNode = new Tone.Synth(synthSettings)
const reverbNode = new Tone.Reverb(reverbSettings)

const channelNode = new Tone.Channel(channelSettings).toDestination()

synthNode.chain(reverbNode, channelNode)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Main synth',
    type: 'ToneSynth',
    node: synthNode,
    settings: synthSettings
  },
  {
    id: generateUniqId(),
    name: 'Reverb',
    type: 'ReverbEffect',
    node: reverbNode,
    settings: reverbSettings
  },
  {
    id: generateUniqId(),
    name: 'Channel',
    type: 'Channel',
    node: channelNode,
    settings: channelSettings
  }
]

const v = 1

const part = new Tone.Part(
  function (time, note) {
    synthNode.triggerAttackRelease(
      note.noteName,
      note.duration,
      time,
      note.velocity
    )
  },
  [
    {
      time: '0:0:0',
      noteName: 'G3',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:1:0',
      noteName: 'G3',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:2:0',
      noteName: 'G3',
      duration: '8n',
      velocity: v
    },
    {
      time: '1:0:0',
      noteName: 'E3',
      duration: '4n',
      velocity: v
    },
    {
      time: '1:2:0',
      noteName: 'E3',
      duration: '4n',
      velocity: v
    }
  ]
)

part.loopEnd = '2m'
part.loop = true

export { instrument, part }

