import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  url:
    'https://tonejs.github.io/audio/drum-samples/loops/organ-echo-chords.mp3',
  mute: true,
  volume: -6,
  autostart: true,
  loop: true
}

// const chorusSettings = {
//   wet: 0,
//   type: 'sine',
//   frequency: 0,
//   delayTime: 0,
//   depth: 0,
//   spread: 0
// }

// const channelSettings = {
//   volume: 0,
//   pan: 0,
//   mute: false,
//   solo: false
// }

// const chorusNode = new Tone.Chorus(chorusSettings).start()

const player = new Tone.Player(playerSettings)
console.log(player.mute)

player.chain(Tone.Destination)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Player Synth',
    type: 'Player',
    node: player,
    settings: playerSettings
  }
  // {
  //   id: generateUniqId(),
  //   name: 'Chorus',
  //   type: 'ChorusEffect',
  //   node: chorusNode,
  //   settings: chorusSettings
  // }
]

export { instrument }
