import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  mute: true,
  autostart: false,
  loop: true
}

const chorusSettings = {
  wet: 0,
  type: 'sine',
  frequency: 0,
  delayTime: 0,
  depth: 0,
  spread: 0
}

const channelSettings = {
  volume: 1,
  mute: true
}

const chorusNode = new Tone.Chorus(chorusSettings).start()
const channelNode = new Tone.Channel(channelSettings).toDestination()

const player = new Tone.Player(
  'https://tonejs.github.io/audio/drum-samples/breakbeat.mp3'
)
// player.autostart = true
// player.loop = true
// player.mute = true

player.chain(chorusNode, channelNode)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Chorus',
    type: 'ChorusEffect',
    node: chorusNode,
    settings: chorusSettings
  }
]

export { instrument }
