import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const synthSettings = {
  volume: 0,
  detune: 0,
  portamento: 0,
  envelope: {
    attack: 0.14,
    attackCurve: 'step',
    decay: 2,
    decayCurve: 'exponential',
    sustain: 0.65,
    release: 0.5,
    releaseCurve: 'bounce'
  },
  oscillator: {
    type: 'fatsine',
    modulationType: 'fatsine',
    // partialCount: 0,
    // partials: [],
    phase: 0,
    harmonicity: 0
  }
}

const autoPannerSettings = {
  wet: 0,
  type: 'sine18',
  frequency: 40,
  depth: 1
}

const chebyshevSettings = {
  wet: 0,
  order: 50,
  oversample: 'none'
}

const pingPongDelaySettings = {
  wet: 0,
  delayTime: 0.25,
  maxDelayTime: 1
}

const pitchShiftSettings = {
  wet: 0.75,
  pitch: 0,
  windowSize: 0.1,
  delayTime: 0,
  feedback: 0.315
}

const vibratoSettings = {
  wet: 0,
  maxDelay: 0.005,
  frequency: 5,
  depth: 0.1,
  type: 'sine'
}

const channelSettings = {
  volume: -80,
  pan: 0,
  mute: false,
  solo: false
}

const synthNode = new Tone.Synth(synthSettings)
const chebyshevNode = new Tone.Chebyshev(chebyshevSettings)
const pingPongDelayNode = new Tone.PingPongDelay(pingPongDelaySettings)
const vibratoNode = new Tone.Vibrato(vibratoSettings)
const channelNode = new Tone.Channel(channelSettings).toDestination()

synthNode.chain(chebyshevNode, pingPongDelayNode, vibratoNode, channelNode)

const v = 1

// prettier-ignore
const partSettings = {
  scale: [
    'A1','C1','D1','E1', 'G1',
    'A2','C2','D2','E2','G2',
    'A3','C3','D3','E3','G3',
    'A4','C4','D4','E4','G4',
    'A5','C5','D5','E5','G5',
    'A6','C6','D6','E6','G6',
    'A7','C7','D7','E7','G7',
    'A8','C8','D8','E8','G8'
  ],
  sequence: [
    {
      time: '0:0:0',
      noteName: 'G5#',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:0:2',
      noteName: 'F5',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:1:0',
      noteName: 'C3',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:2:0',
      noteName: 'G5#',
      duration: '8n',
      velocity: v
    },
    {
      time: '0:3:2',
      noteName: 'G5#',
      duration: '8n',
      velocity: v
    },
    {
      time: '1:0:0',
      noteName: 'F3',
      duration: '8n',
      velocity: v
    },
    {
      time: '1:0:2',
      noteName: 'C3',
      duration: '8n',
      velocity: v
    },
    {
      time: '1:2:0',
      noteName: 'G5#',
      duration: '8n',
      velocity: v
    }
  ]
}

const partNode = new Tone.Part(function (time, note) {
  synthNode.triggerAttackRelease(
    note.noteName,
    note.duration,
    time,
    note.velocity
  )
}, [])

partNode.loopEnd = '2m'
partNode.loop = true

const instrument = [
  {
    id: generateUniqId(),
    name: 'Day Synth',
    type: 'ToneSynth',
    node: synthNode,
    settings: synthSettings
  },
  {
    id: generateUniqId(),
    name: 'Chebyshev',
    type: 'ChebyshevEffect',
    node: chebyshevNode,
    settings: chebyshevSettings
  },
  {
    id: generateUniqId(),
    name: 'Ping Pong Delay',
    type: 'PingPongDelayEffect',
    node: pingPongDelayNode,
    settings: pingPongDelaySettings
  },
  {
    id: generateUniqId(),
    name: 'Vibrato',
    type: 'VibratoEffect',
    node: vibratoNode,
    settings: vibratoSettings
  },
  {
    id: generateUniqId(),
    name: 'Channel',
    type: 'Channel',
    node: channelNode,
    settings: channelSettings
  },
  {
    id: generateUniqId(),
    name: 'Sequencer',
    type: 'Sequencer',
    node: partNode,
    settings: partSettings
  }
]

export { instrument }
