import * as Tone from 'tone'
import { generateUniqId } from '../utilities'

const playerSettings = {
  mute: true,
  volume: 0,
  autostart: true,
  loop: true
}

const chorusSettings = {
  wet: 0,
  type: 'sine',
  frequency: 0,
  delayTime: 0,
  depth: 0,
  spread: 0
}

const chorusNode = new Tone.Chorus(chorusSettings).start()

const player = new Tone.Player(
  playerSettings,
  'https://tonejs.github.io/audio/drum-samples/breakbeat.mp3'
)
// player.autostart = true
// player.loop = true
//
// player.mute = true

console.log(player.mute)

player.chain(chorusNode, Tone.Destination)

const instrument = [
  {
    id: generateUniqId(),
    name: 'Chorus',
    type: 'ChorusEffect',
    node: chorusNode,
    settings: chorusSettings
  }
]

export { instrument }
