import * as Tone from 'tone';
import { generateUniqId } from '../utilities';
var playerSettings = {
  url: 'https://tonejs.github.io/audio/drum-samples/Techno/hihat.mp3',
  mute: true,
  volume: 1,
  autostart: true,
  loop: true,
  loopStart: 0.2,
  loopEnd: 0.6,
  playbackRate: 0.8
};
var player = new Tone.Player(playerSettings);
console.log(player.mute);
player.chain(Tone.Destination);
var instrument = [{
  id: generateUniqId(),
  name: 'Hihat Sound',
  type: 'Player',
  node: player,
  settings: playerSettings
}];
export { instrument };
